import React from "react";
import Booking from "./components/Booking.js";
import SearchForm from "./components/SearchForm.js";

function App() {
  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(tomorrow.getDate() + 1);

  function formatDate(date) {
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    if (month < 10) {
      return `${year}-0${month}-${day}`;
    } else {
      return `${year}-${month}-${day}`;
    }
  }

  const [searchData, setSearchData] = React.useState({
    city: "Madrid",
    budget: 100,
    checkin: todayDate,
    checkinFormatted: formatDate(todayDate),
    checkout: todayDate,
    checkoutFormatted: formatDate(todayDate),
  });

  const [submitted, setSubmitted] = React.useState(false);

  const [bookingResults, setBookingResults] = React.useState([]);

  return (
    <div>
      <SearchForm
        searchData={searchData}
        setSearchData={setSearchData}
        submitted={submitted}
        setSubmitted={setSubmitted}
        setBookingResults={setBookingResults}
      />
      {submitted && <Booking data={searchData} results={bookingResults} />}
    </div>
  );
}

export default App;

