import React from "react";
import "../App.css";

export default function SortAndFilter(props) {
  function handleChangeSort(event) {
    props.setSort(event.target.value);
  }

  function sortListings(event) {
    event.preventDefault();
    const listings = props.results.results;

    if (props.sort === "Price (Low to High)") {
      const sortedListings = {
        results: listings.sort((a, b) => (a.price > b.price ? 1 : -1)),
      };

      props.setResults(() => sortedListings);
    } else if (props.sort === "Price (High to Low)") {
      const sortedListings = {
        results: listings.sort((a, b) => (a.price < b.price ? 1 : -1)),
      };
      props.setResults(() => sortedListings);
    } else if (props.sort === "Rating") {
      const sortedListings = {
        results: listings.sort((a, b) => (a.rating < b.rating ? 1 : -1)),
      };
      props.setResults(() => sortedListings);
    }
  }

  function filterListings(checked, filterBy) {
    let filteredResults = [];

    const formattedFilter =
      filterBy === "bookingcom" ? "Booking.com" : "Airbnb";

    if (!checked) {
      const tempResults = props.filteredResults;
      filteredResults = tempResults.filter(
        (result) => result.source !== formattedFilter
      );
      props.setFilteredResults(() => filteredResults);
    } else if (checked) {
      const tempResults = props.results.results;
      tempResults.forEach((result) => {
        if (result.source === formattedFilter && checked) {
          filteredResults.push(result);
        }
      });
      props.setFilteredResults((prevFiltered) => [
        ...prevFiltered,
        ...filteredResults,
      ]);
    }
  }

  function filterListingsByRating(checked, name) {
    let filteredResults = [];

    const rating = () => {
      switch (name) {
        case "rating9":
          return 9;
        case "rating8":
          return 8;
        case "rating7":
          return 7;
        case "rating6":
          return 6;
      }
    };

    //check if filtered by source
    if (!props.filteredBySource.bookingcom && !props.filteredBySource.airbnb) {
      if (checked) {
        const tempResults = props.results.results;
        tempResults.forEach((result) => {
          if (result.rating >= rating()) {
            filteredResults.push(result);
          }
        });
        props.setFilteredResults(() => filteredResults);
      } else {
        const tempResults = props.filteredResults;
        filteredResults = tempResults.filter(
          (result) => result.rating < rating()
        );
        props.setFilteredResults(() => filteredResults);
      }
    } else {
      console.log("***filtered", props.filteredResults);
      if (checked) {
        const tempResults = props.filteredResults;
        tempResults.forEach((result) => {
          if (result.rating >= rating()) {
            filteredResults.push(result);
          }
        });
        props.setFilteredResults((prevResults) => [
          ...prevResults,
          filteredResults,
        ]);
      }
      console.log("*****filtered by source");
    }
  }

  function handleFilterBySourceChange(event) {
    const { name, checked } = event.target;
    props.setFilteredBySource({
      ...props.filteredBySource,
      [name]: checked,
    });
    filterListings(checked, name);
  }

  function handleFilterByRatingChange(event) {
    const { name, checked } = event.target;
    props.setFilteredByRating({
      ...props.filteredByRating,
      [name]: checked,
    });
    filterListingsByRating(checked, name);
  }

  return (
    <div className="sort-and-filters">
      <form className="sort-form">
        <div className="sortBy-div">
          <select
            className="select-input"
            value={props.sort}
            onChange={handleChangeSort}
            name="sortBy"
          >
            <option value="" disabled={true}>
              Sort by:
            </option>
            <option key="lowToHigh">Price (Low to High)</option>
            <option key="highToLow">Price (High to Low)</option>
            <option key="rating">Rating</option>
          </select>
          <button onClick={sortListings} className="sort-button">
            Sort
          </button>
        </div>
      </form>
      <div className="filter-div">
        <h4 className="filterby-text">Filter by:</h4>
        <div className="filter-row">
          <fieldset className="rating-fieldset">
            <legend className="filter-legend">Rating:</legend>
            <input
              type="checkbox"
              id="rating9"
              checked={props.filteredByRating.rating9}
              onChange={handleFilterByRatingChange}
              name="rating9"
            />
            <label htmlFor="rating9">Amazing 9+</label>
            <br />
            <input
              type="checkbox"
              id="rating8"
              checked={props.filteredByRating.rating8}
              onChange={handleFilterByRatingChange}
              name="rating8"
            />
            <label htmlFor="airbnb">Great 8+</label>
            <br />
            <input
              type="checkbox"
              id="rating7"
              checked={props.filteredByRating.rating7}
              onChange={handleFilterByRatingChange}
              name="rating7"
            />
            <label htmlFor="rating7">Good 7+</label>
            <br />
            <input
              type="checkbox"
              id="rating6"
              checked={props.filteredByRating.rating6}
              onChange={handleFilterByRatingChange}
              name="rating6"
            />
            <label htmlFor="rating6">Decent 6+</label>
          </fieldset>
          <fieldset className="source-fieldset">
            <legend className="filter-legend">Source:</legend>
            <input
              type="checkbox"
              id="bookingcom"
              checked={props.filteredBySource.bookingcom}
              onChange={handleFilterBySourceChange}
              name="bookingcom"
            />
            <label htmlFor="bookingcom">Booking.com</label>
            <br />
            <input
              type="checkbox"
              id="airbnb"
              checked={props.filteredBySource.airbnb}
              onChange={handleFilterBySourceChange}
              name="airbnb"
            />
            <label htmlFor="airbnb">Airbnb</label>
          </fieldset>
        </div>
      </div>
    </div>
  );
}
