import React from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default function SearchForm(props) {
  function handleChange(event) {
    props.setSearchData((prevFormData) => {
      return {
        ...prevFormData,
        [event.target.name]: event.target.value,
      };
    });
  }

  function formatDate(date) {
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    if (month < 10) {
      return `${year}-0${month}-${day}`;
    } else {
      return `${year}-${month}-${day}`;
    }
  }

  function handleDateChange(event, date) {
    const day = date.getDate();
    const month = date.getMonth();
    const year = date.getFullYear();
    const name = [event.target.name];
    const formatted = name + "Formatted";
    props.setSearchData((prevFormData) => {
      return {
        ...prevFormData,
        name: date,
        formatted: formatDate(date),
      };
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetch(
      `http://localhost:8000/api/listings/?city=${props.searchData.city}&budget=${props.searchData.budget}`,
      {
        method: "GET",
      }
    )
      .then((response) => response.json())
      .then((result) => {
        props.setBookingResults(result);
        props.setSubmitted(true);
      });
  }

  console.log("*****booking info", props.searchData);
  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="city">City:</label>
      <input
        type="text"
        placeholder="City"
        onChange={handleChange}
        name="city"
        value={props.searchData.city}
      ></input>
      <label htmlFor="budget">Budget:</label>
      <input
        type="number"
        placeholder="100"
        onChange={handleChange}
        name="budget"
        value={props.searchData.budget}
      ></input>
      <label htmlFor="checkin">Checkin:</label>
      <DatePicker
        selected={props.searchData.checkin}
        onChange={(event, value) => handleDateChange(value)}
        name="checkin"
      />
      <DatePicker
        selected={props.searchData.checkout}
        onChange={(event, value) => handleDateChange(value)}
        name="checkout"
      />
      <button>Submit</button>
    </form>
  );
}
