            />
            <label htmlFor="rating7+">Decent: 6+</label>
            <br />
            <button
              onClick={() => props.setFilterByRating("")}
              className="clear-filters-button"
            >
              Clear Filters
            </button>
          </fieldset>
          <fieldset className="source-fieldset">
            <legend className="filter-legend">Source:</legend>
            <input
              type="radio"
              id="bookingcom"
