import React from "react";
import "../App.css";
import SortAndFilter from "./SortAndFilter";

export default function Results(props) {
  const listings = props.results.results;

  const [sort, setSort] = React.useState("");

  const [filterByRating, setFilterByRating] = React.useState("");

  const [filterBySource, setFilterBySource] = React.useState("");

  const [filteredBySource, setFilteredBySource] = React.useState({
    bookingcom: false,
    airbnb: false,
  });

  const [filteredResultsBySource, setFilteredResultsBySource] = React.useState(
    []
  );

  const [filteredByRating, setFilteredByRating] = React.useState(false);

  const [filteredResultsByRating, setFilteredResultsByRating] = React.useState(
    []
  );

  const filtered =
    filteredBySource.bookingcom || filteredBySource.airbnb || filteredByRating;

  console.log("****filtered", filtered);

  // const [sortedAndFiltered, setSortedAndFiltered] = React.useState({
  //   sortedLowToHigh: false,
  //   sortedHighToLow: false,
  //   sortedRating: false,
  //   filteredSource: false,
  //   filteredRating: false,
  // });

  const getRatingText = (rating) => {
    if (rating >= 9) {
      return (
        <div className="rating-div">
          <span className="rating-text">Amazing</span>{" "}
          <span className="rating">{rating}</span>
        </div>
      );
    } else if (rating >= 8.0) {
      return (
        <div className="rating-div">
          <span className="rating-text">Great</span>{" "}
          <span className="rating">{rating}</span>
        </div>
      );
    } else if (rating >= 8) {
      return (
        <div className="rating-div">
          <span className="rating-text">Good</span>{" "}
          <span className="rating">{rating}</span>
        </div>
      );
    } else if (rating >= 7) {
      return (
        <div className="rating-div">
          <span className="rating-text">Decent</span>{" "}
          <span className="rating">{rating}</span>
        </div>
      );
    } else {
      return (
        <div className="rating-div">
          <span className="rating">{rating}</span>
        </div>
      );
    }
  };

  return (
    <div>
      <h1 className="results-h1">Browse Results</h1>
      <SortAndFilter
        sort={sort}
        setSort={setSort}
        filterByRating={filterByRating}
        setFilterByRating={setFilterByRating}
        filterBySource={filterBySource}
        setFilterBySource={setFilterBySource}
        filteredBySource={filteredBySource}
        setFilteredBySource={setFilteredBySource}
        filteredResultsBySource={filteredResultsBySource}
        setFilteredResultsBySource={setFilteredResultsBySource}
        filteredByRating={filteredByRating}
        setFilteredByRating={setFilteredByRating}
        filteredResultsByRating={filteredResultsByRating}
        setFilteredResultsByRating={setFilteredResultsByRating}
        results={props.results}
        setResults={props.setResults}
        submitted={props.submitted}
      />
      <div className="results">
        {!filtered &&
          listings &&
          listings.map((listing) => (
            <div className="listing" key={listing.key}>
              <div className="listing-name-rating">
                <h4 className="listing-name">{listing.name}</h4>
                {listing.rating ? getRatingText(listing.rating) : ""}
              </div>

              <div className="listing-row">
                <img
                  className="listing-image"
                  src={listing.img}
                  alt={listing.name}
                />
                <div className="listing-info">
                  <p className="listing-source">Source: {listing.source}</p>
                  <p className="listing-price">
                    Price per night: {listing.currency} ${listing.price}
                  </p>
                  <p className="listing-roomType">{listing.roomType}</p>
                  <span className="listing-unitConfiguration">
                    {listing.unitConfiguration && (
                      <p>{listing.unitConfiguration}</p>
                    )}
                  </span>
                  <p className="listing-bedType">{listing.bedType}</p>
                  <a className="listing-url" href={listing.url} target="_blank">
                    View listing
                  </a>
                </div>
              </div>
            </div>
          ))}
      </div>
      <div className="results">
        {filteredBySource &&
          filteredResultsBySource.results &&
          filteredResultsBySource.results.map((listing) => (
            <div className="listing" key={listing.key}>
              <div className="listing-name-rating">
                <h4 className="listing-name">{listing.name}</h4>
                {listing.rating ? getRatingText(listing.rating) : ""}
              </div>

              <div className="listing-row">
                <img
                  className="listing-image"
                  src={listing.img}
                  alt={listing.name}
                />
                <div className="listing-info">
                  <p className="listing-source">Source: {listing.source}</p>
                  <p className="listing-price">
                    Price per night: {listing.currency} ${listing.price}
                  </p>
                  <p className="listing-roomType">{listing.roomType}</p>
                  <span className="listing-unitConfiguration">
                    {listing.unitConfiguration && (
                      <p>{listing.unitConfiguration}</p>
                    )}
                  </span>
                  <p className="listing-bedType">{listing.bedType}</p>
                  <a className="listing-url" href={listing.url} target="_blank">
                    View listing
                  </a>
                </div>
              </div>
            </div>
          ))}
      </div>
      <div className="results">
        {filteredByRating &&
          filteredResultsByRating.results &&
          filteredResultsByRating.results.map((listing) => (
            <div className="listing" key={listing.key}>
              <div className="listing-name-rating">
                <h4 className="listing-name">{listing.name}</h4>
                {listing.rating ? getRatingText(listing.rating) : ""}
              </div>

              <div className="listing-row">
                <img
                  className="listing-image"
                  src={listing.img}
                  alt={listing.name}
                />
                <div className="listing-info">
                  <p className="listing-source">Source: {listing.source}</p>
                  <p className="listing-price">
                    Price per night: {listing.currency} ${listing.price}
                  </p>
                  <p className="listing-roomType">{listing.roomType}</p>
                  <span className="listing-unitConfiguration">
                    {listing.unitConfiguration && (
                      <p>{listing.unitConfiguration}</p>
                    )}
                  </span>
                  <p className="listing-bedType">{listing.bedType}</p>
                  <a className="listing-url" href={listing.url} target="_blank">
                    View listing
                  </a>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}
