import React from "react";
import "../App.css";

export default function SortAndFilter(props) {
  function handleChangeSort(event) {
    props.setSort(event.target.value);
  }

  // function handleFilterChange(event) {
  //   const { name, checked } = event.target;
  //   props.setFilter((prevFilters) => {
  //     return {
  //       ...prevFilters,
  //       [name]: checked,
  //     };
  //   });
  // }

  return (
    <div className="sort-and-filters">
      <form className="sort-form">
        <div className="sortBy-div">
          <select
            className="select-input"
            value={props.sort}
            onChange={handleChangeSort}
            name="sortBy"
          >
            <option value="">Sort by:</option>
            <option key="lowToHigh">Price (Low to High)</option>
            <option key="highToLow">Price (High to Low)</option>
            <option key="rating">Rating</option>
          </select>
          <button className="sort-button">Sort</button>
        </div>
      </form>
      <div className="filter-div">
        <fieldset>
          <legend className="filterBy">Filter by:</legend>
          <input
            type="radio"
            id="rating9+"
            name="filterBy"
            value="rating9+"
            checked={props.filter === "rating9+"}
            onChange={handleChange}
          />
          <label htmlFor="unemployed">Unemployed</label>
          <br />

          <input
            type="radio"
            id="part-time"
            name="employment"
            value="part-time"
            checked={formData.employment === "part-time"}
            onChange={handleChange}
          />
          <label htmlFor="part-time">Part-time</label>
          <br />

          <input
            type="radio"
            id="full-time"
            name="employment"
            value="full-time"
            checked={formData.employment === "full-time"}
            onChange={handleChange}
          />
          <label htmlFor="full-time">Full-time</label>
          <br />
        </fieldset>
      </div>
      {/* <div className="filter-box">
          <input
            type="checkbox"
            id="filterByRating"
            checked={props.filters.filterByRating}
            onChange={handleFilterChange}
            name="filterByRating"
          />
          <label htmlFor="filterBySource">Rating</label>
        </div>
        <div className="filter-box">
          <input
            type="checkbox"
            id="filterBySource"
            checked={props.filters.filterBySource}
            onChange={handleFilterChange}
            name="filterBySource"
          />
          <label htmlFor="filterBySource">Source</label>
        </div>
      </div> */}
    </div>
  );
}
