import React from "react";
import "../App.css";

export default function SortAndFilter(props) {
  function handleChangeSort(event) {
    props.setSort(event.target.value);
  }

  function handleFilterByRatingChange(event) {
    props.setFilterByRating(event.target.value);
  }

  function handleFilterBySourceChange(event) {
    const { name, checked } = event.target;
    props.setFilteredBySource((prevFormData) => {
      return {
        ...prevFormData,
        [name]: checked,
      };
    });

    fetch(
      `http://localhost:8000/api/filter/?filterBySource=${name}&listings=${JSON.stringify(
        props.results.results
      )}`,
      {
        method: "GET",
      }
    )
      .then((response) => response.json())
      .then((result) => {
        console.log("**result", result);
      });
  }

  // console.log("***filtered by source", props.filteredBySource);

  function sortListings(event) {
    event.preventDefault();

    const listings = props.filteredBySource
      ? props.filteredResultsBySource.results
      : props.results.results;

    if (props.sort === "Price (Low to High)") {
      const sortedListings = {
        results: listings.sort((a, b) => (a.price > b.price ? 1 : -1)),
      };

      props.filteredBySource
        ? props.setFilteredResultsBySource(() => sortedListings)
        : props.setResults(() => sortedListings);
    } else if (props.sort === "Price (High to Low)") {
      const sortedListings = {
        results: listings.sort((a, b) => (a.price < b.price ? 1 : -1)),
      };
      props.filteredBySource
        ? props.setFilteredResultsBySource(() => sortedListings)
        : props.setResults(() => sortedListings);
    } else if (props.sort === "Rating") {
      const sortedListings = {
        results: listings.sort((a, b) => (a.rating < b.rating ? 1 : -1)),
      };
      props.filteredBySource
        ? props.setFilteredResultsBySource(() => sortedListings)
        : props.setResults(() => sortedListings);
    }
  }

  function filterBySource() {
    // props.setFilteredBySource((filtered) => !filtered);
    // console.log("****props.filtered", props.filteredBySource);
    const listings = props.results.results;

    console.log("****props.filterBySource", props.filterBySource);

    if (props.filterBySource === "bookingcom") {
      const filtered = {
        results: listings.filter((listing) => {
          return listing.source === "Booking.com";
        }),
      };
      props.setFilteredResultsBySource(() => filtered);
    } else if (props.filterBySource === "airbnb") {
      const filtered = {
        results: listings.filter((listing) => {
          return listing.source === "Airbnb";
        }),
      };
      props.setFilteredResultsBySource(() => filtered);
    }
  }

  function filterByRating() {
    props.setFilteredByRating((filtered) => !filtered);
    const listings = props.results.results;

    if (props.filterByRating === "rating9+") {
      const filtered = {
        results: listings.filter((listing) => {
          return listing.rating > 9;
        }),
      };
      props.setFilteredResultsByRating(() => filtered);
    } else if (props.filterByRating === "rating8+") {
      const filtered = {
        results: listings.filter((listing) => {
          return listing.rating > 8;
        }),
      };
      props.setFilteredResultsByRating(() => filtered);
    } else if (props.filterByRating === "rating7+") {
      const filtered = {
        results: listings.filter((listing) => {
          return listing.rating > 7;
        }),
      };
      props.setFilteredResultsByRating(() => filtered);
    } else if (props.filterByRating === "rating6+") {
      const filtered = {
        results: listings.filter((listing) => {
          return listing.rating > 6;
        }),
      };
      props.setFilteredResultsByRating(() => filtered);
    }
  }

  function clearRatingFilter() {
    props.setFilterByRating(() => "");
    props.setFilteredByRating((filtered) => !filtered);
  }

  function clearSourceFilter() {
    props.setFilterBySource(() => "");
    props.setFilteredBySource((filtered) => !filtered);
  }

  function onFilterClick(event) {
    event.preventDefault();
    if (props.filterBySource) {
      filterBySource();
    } else {
      filterByRating();
    }
  }

  return (
    <div className="sort-and-filters">
      <form className="sort-form">
        <div className="sortBy-div">
          <select
            className="select-input"
            value={props.sort}
            onChange={handleChangeSort}
            name="sortBy"
          >
            <option value="" disabled={true}>
              Sort by:
            </option>
            <option key="lowToHigh">Price (Low to High)</option>
            <option key="highToLow">Price (High to Low)</option>
            <option key="rating">Rating</option>
          </select>
          <button onClick={sortListings} className="sort-button">
            Sort
          </button>
        </div>
      </form>
      <div className="filter-div">
        <h4 className="filterby-text">Filter by:</h4>
        <div className="filter-row">
          <fieldset className="rating-fieldset">
            <legend className="filter-legend">Rating:</legend>
            <input
              type="radio"
              id="rating9+"
              name="filterByRating"
              value="rating9+"
              checked={props.filterByRating === "rating9+"}
              onChange={handleFilterByRatingChange}
            />
            <label htmlFor="rating9+">Amazing: 9+</label>
            <br />

            <input
              type="radio"
              id="rating8+"
              name="filterByRating"
              value="rating8+"
              checked={props.filterByRating === "rating8+"}
              onChange={handleFilterByRatingChange}
            />
            <label htmlFor="rating8+">Great: 8+</label>
            <br />

            <input
              type="radio"
              id="7+"
              name="filterByRating"
              value="rating7+"
              checked={props.filterByRating === "rating7+"}
              onChange={handleFilterByRatingChange}
            />
            <label htmlFor="rating7+">Good: 7+</label>
            <br />
            <input
              type="radio"
              id="6+"
              name="filterByRating"
              value="rating6+"
              checked={props.filterByRating === "rating6+"}
              onChange={handleFilterByRatingChange}
            />
            <label htmlFor="rating7+">Decent: 6+</label>
            <br />
            <button
              onClick={clearRatingFilter}
              className="clear-filters-button"
            >
              Clear Filters
            </button>
          </fieldset>
          <fieldset className="source-fieldset">
            <legend className="filter-legend">Source:</legend>
            {/* <input
              type="radio"
              id="bookingcom"
              name="filterBySource"
              value="bookingcom"
              checked={props.filterBySource === "bookingcom"}
              onChange={handleFilterBySourceChange}
            />

            <label htmlFor="rating7+">Booking.com</label>
            <br />
            <input
              type="radio"
              id="airbnb"
              name="filterBySource"
              value="airbnb"
              checked={props.filterBySource === "airbnb"}
              onChange={handleFilterBySourceChange}
            />
            <label htmlFor="rating7+">AirBnB</label>
            <br /> */}
            <input
              type="checkbox"
              id="bookingcom"
              checked={props.filteredBySource.bookingcom}
              onChange={handleFilterBySourceChange}
              name="bookingcom"
            />
            <label htmlFor="bookingcom">Booking.com</label>
            <br />
            <input
              type="checkbox"
              id="airbnb"
              checked={props.filteredBySource.airbnb}
              onChange={handleFilterBySourceChange}
              name="airbnb"
            />
            <label htmlFor="airbnb">Airbnb</label>
            <button
              name="filterBySource"
              onClick={clearSourceFilter}
              className="clear-filters-button"
            >
              Clear Filters
            </button>
          </fieldset>
        </div>
        <button onClick={onFilterClick} className="sort-button">
          Filter
        </button>
      </div>
    </div>
  );
}
