import React from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default function SearchForm(props) {
  function handleChange(event) {
    props.setSearchData((prevFormData) => {
      return {
        ...prevFormData,
        [event.target.name]: event.target.value,
      };
    });
  }

  function formatDate(date) {
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    if (month < 10) {
      return `${year}-0${month}-${day}`;
    } else {
      return `${year}-${month}-${day}`;
    }
  }

  function handleCheckinChange(date) {
    props.setSearchData((prevFormData) => {
      return {
        ...prevFormData,
        checkin: date,
        checkinFormatted: formatDate(date),
      };
    });
  }

  function handleCheckoutChange(date) {
    props.setSearchData((prevFormData) => {
      return {
        ...prevFormData,
        checkout: date,
        checkoutFormatted: formatDate(date),
      };
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetch(
      `http://localhost:8000/api/listings/?city=${props.searchData.city}&budget=${props.searchData.budget}&checkin=${props.searchData.checkinFormatted}&checkout=${props.searchData.checkoutFormatted}`,
      {
        method: "GET",
      }
    )
      .then((response) => response.json())
      .then((result) => {
        props.setBookingResults(result);
        props.setSubmitted(true);
      });
  }

  function setNewDate(date) {
    const tomorrow = new Date(date);
    tomorrow.setDate(tomorrow.getDate() + 1);

    return tomorrow;
  }

  return (
    <div className="search-form">
      <form className="form" onSubmit={handleSubmit}>
        <div className="row">
          <div className="column">
            <div className="city-div">
              <label htmlFor="city">Enter your destination city:</label>
              <br />
              <input
                className="search-input"
                type="text"
                placeholder="City"
                onChange={handleChange}
                name="city"
                value={props.searchData.city}
              ></input>
            </div>
          </div>
          <div className="column">
            <div className="budget-div">
              <label htmlFor="budget">Enter your budget (USD):</label>
              <br />
              <input
                className="search-input"
                type="number"
                placeholder="100"
                onChange={handleChange}
                name="budget"
                value={props.searchData.budget}
              ></input>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="column">
            <div className="checkin-div">
              <label htmlFor="checkin">Check-in date:</label>
              <br />
              <DatePicker
                className="date-input"
                selected={props.searchData.checkin}
                onChange={(value) => handleCheckinChange(value)}
                name="checkin"
                minDate={new Date()}
              />
            </div>
          </div>
          <div className="column">
            <div className="checkout-div">
              <label htmlFor="checkin">Check-out date:</label>
              <br />
              <DatePicker
                minDate={(date) => setNewDate(date)}
                className="date-input"
                selected={props.searchData.checkout}
                onChange={(value) => handleCheckoutChange(value)}
                name="checkout"
              />
            </div>
          </div>
        </div>
        <button>Submit</button>
      </form>
    </div>
  );
}
