import React from "react";
import "../App.css";

export default function SortAndFilter(props) {
  function handleChangeSort(event) {
    props.setSort(event.target.value);
  }

  // function handleFilterChange(event) {
  //   const { name, checked } = event.target;
  //   props.setFilter((prevFilters) => {
  //     return {
  //       ...prevFilters,
  //       [name]: checked,
  //     };
  //   });
  // }

  return (
    <div className="sort-and-filters">
      <form className="sort-form">
        <div className="sortBy-div">
          <select
            className="select-input"
            value={props.sort}
            onChange={handleChangeSort}
            name="sortBy"
          >
            <option value="">Sort by:</option>
            <option key="lowToHigh">Price (Low to High)</option>
            <option key="highToLow">Price (High to Low)</option>
            <option key="rating">Rating</option>
          </select>
          <button className="sort-button">Sort</button>
        </div>
      </form>
      <div className="filter-div">
        <h4>Filter by:</h4>
        <fieldset>
          <legend className="filter-legend">Rating:</legend>
          <input
            type="radio"
            id="rating9+"
            name="filterBy"
            value="rating9+"
            checked={props.filterByRating === "rating9+"}
            // onChange={handleChange}
          />
          <label htmlFor="rating9+">Amazing: 9+</label>
          <br />

          <input
            type="radio"
            id="rating8+"
            name="filterBy"
            value="rating8+"
            checked={props.filterByRating === "rating8+"}
            // onChange={handleChange}
          />
          <label htmlFor="rating8+">Great: 8+</label>
          <br />

          <input
            type="radio"
            id="7+"
            name="filterBy"
            value="rating7+"
            checked={props.filterByRating === "rating7+"}
            // onChange={handleChange}
          />
          <label htmlFor="rating7+">Good: 7+</label>
          <br />
          <input
            type="radio"
            id="6+"
            name="filterBy"
            value="rating6+"
            checked={props.filterByRating === "rating6+"}
            // onChange={handleChange}
          />
          <label htmlFor="rating7+">Decent: 6+</label>
          <br />
        </fieldset>
        <fieldset>
          <legend className="filter-legend">Source:</legend>
          <input
            type="radio"
            id="bookingcom"
            name="filterBy"
            value="bookingcom"
            checked={props.filterBySource === "bookingcom"}
            // onChange={handleChange}
          />
          <label htmlFor="rating7+">Decent: 6+</label>
        </fieldset>
      </div>
      {/* <div className="filter-box">
          <input
            type="checkbox"
            id="filterByRating"
            checked={props.filters.filterByRating}
            onChange={handleFilterChange}
            name="filterByRating"
          />
          <label htmlFor="filterBySource">Rating</label>
        </div>
        <div className="filter-box">
          <input
            type="checkbox"
            id="filterBySource"
            checked={props.filters.filterBySource}
            onChange={handleFilterChange}
            name="filterBySource"
          />
          <label htmlFor="filterBySource">Source</label>
        </div>
      </div> */}
    </div>
  );
}
