import React from "react";
import "../App.css";

export default function Booking(props) {
  const listings = props.results.results;

  const [sort, setSort] = React.useState("");

  const [filters, setFilters] = React.useState({
    filterByAccommodation: false,
    filterByRating: false,
    filterBySource: false,
  });

  function handleChangeSort(event) {
    setSort(event.target.value);
  }

  function handleFilterChange(event) {
    console.log("***event", event.target.value);
    const { name, value, type, checked } = event.target;
    setFormData((prevFormData) => {
      return {
        ...prevFormData,
        [name]: checked,
      };
    });
  }

  console.log("*****filter", filters);

  // function sortByPrice() {
  //   listings.sort((a, b) => (a.price < b.price ? 1 : -1));
  // }

  return (
    <div>
      <h1 className="results-h1">Browse Results</h1>
      <div className="sort-and-filters">
        <form className="sort-form">
          <div className="sortBy-div">
            <select
              className="select-input"
              value={sort}
              onChange={handleChangeSort}
              name="sortBy"
            >
              <option value="">Sort by:</option>
              <option key="lowToHigh">Price (Low to High)</option>
              <option key="highToLow">Price (High to Low)</option>
              <option key="rating">Rating</option>
            </select>
            <button className="sort-button">Sort</button>
          </div>
        </form>
        <div className="filter-div">
          <h4 className="filterBy">Filter by:</h4>
          <input
            type="checkbox"
            id="filterByRating"
            checked={filters.filterByRating}
            onChange={handleFilterChange}
            name="filterByRating"
          />
          <input
            type="checkbox"
            id="filterBySource"
            checked={filters.filterBySource}
            onChange={handleFilterChange}
            name="filterBySource"
          />
          <label htmlFor="filterByRating">Rating</label>
        </div>
      </div>
      <div className="results">
        {listings &&
          listings.map((listing) => (
            <div className="listing" key={listing.key}>
              <h4 className="listing-name">{listing.name}</h4>
              <div className="listing-row">
                <img
                  className="listing-image"
                  src={listing.img}
                  alt={listing.name}
                />
                <div className="listing-info">
                  <p className="listing-source">Source: {listing.source}</p>
                  <p className="listing-price">
                    Price per night: {listing.price}
                  </p>
                  <p className="listing-roomType">{listing.roomType}</p>
                  <span className="listing-unitConfiguration">
                    {listing.unitConfiguration && (
                      <p>{listing.unitConfiguration}</p>
                    )}
                  </span>
                  <p className="listing-bedType">{listing.bedType}</p>
                  <a className="listing-url" href={listing.url} target="_blank">
                    View listing
                  </a>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}
